//Retorna verdade(true) se apenas um dos argumentos é true

function umOuOutro(a, b) {
  if ((a, b) == (true, true)) {
    return false;
  } else if ((a, b) == (false, true)) {
    return true;
  } else if ((a, b) == (true, false)) {
    return true;
  } else if ((a, b) == (false, false)) {
    return false;
  }
}

console.log(umOuOutro(1 == 1, 2 > 6)); //true

//correçao
function umOuOutro2(a, b) {
  if ((a === true && b === false) || (a === false && b === true)) {
    return true;
  }
  //ou
  if (a === b) {
    return false;
  }
  //ou
  if ((a && b) || (!a && !b)) {
    return false;
  } else {
    return true;
  }
  //ou
  if (a ^ b) {
    //XOR
    return true;
  }
}

